@Composable
fun LineChart_1() {
    var selectedUnit by remember { mutableStateOf("kg") }
    var expanded by remember { mutableStateOf(false) }

    val unitOptions = listOf("kg", "lb")

    Column(modifier = Modifier.fillMaxWidth()) {
        // Unit Selection Dropdown
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(horizontal = 16.dp, vertical = 8.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "체중 단위:",
                modifier = Modifier.padding(end = 8.dp),
                fontSize = 16.sp,
                color = Color.Black
            )

            Box {
                Row(
                    modifier = Modifier
                        .clickable { expanded = !expanded }
                        .background(
                            Color.LightGray.copy(alpha = 0.3f),
                            shape = androidx.compose.foundation.shape.RoundedCornerShape(8.dp)
                        )
                        .padding(horizontal = 12.dp, vertical = 8.dp),
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    Text(
                        text = selectedUnit,
                        fontSize = 14.sp,
                        color = Color.Black
                    )
                    Icon(
                        imageVector = Icons.Default.ArrowDropDown,
                        contentDescription = "Unit Dropdown",
                        tint = Color.Black,
                        modifier = Modifier.size(20.dp)
                    )
                }

                DropdownMenu(
                    expanded = expanded,
                    onDismissRequest = { expanded = false }
                ) {
                    unitOptions.forEach { unit ->
                        DropdownMenuItem(
                            text = {
                                Text(
                                    text = unit,
                                    fontSize = 14.sp
                                )
                            },
                            onClick = {
                                selectedUnit = unit
                                expanded = false
                            }
                        )
                    }
                }
            }
        }

        // Weight Chart
        Box(modifier = Modifier.fillMaxWidth().height(250.dp)) {
            LineChart(
                modifier = Modifier.fillMaxWidth().height(250.dp),
                data = weightHealthData.transform(
                    massUnit = when (selectedUnit) {
                        "kg" -> MassUnit.KILOGRAM
                        "lb" -> MassUnit.POUND
                        else -> MassUnit.KILOGRAM
                    },
                    timeUnit = TimeUnitGroup.DAY,
                    aggregationType = AggregationType.DAILY_AVERAGE
                ),
                title = "일별 체중 변화",
                unit = selectedUnit,
                yLabel = "체중 ($selectedUnit)",
                xLabel = "날짜",
                minY = when (selectedUnit) {
                    "kg" -> 50.0
                    "lb" -> 110.0
                    else -> 50.0
                },
                lineColor = Primary_Purple,
                strokeWidth = 12f,
                showPoint = true,
                pointRadius = Pair(6.dp, 3.dp),
                showValue = false,
                windowSize = 7,
                interactionType = InteractionType.Line.POINT,
                yAxisPosition = YAxisPosition.RIGHT
            )
        }
    }
}

